css:
@-moz-document url-prefix("https://webmail.saunalahti.fi") {
html body table tbody tr td table tbody tr td.mailbg:nth-of-type(3),
div.header,
table tbody tr:nth-child(2) td span.txt
{
  display:none !important;
}

a, input[type="submit"]
{
  color: #f55724 !important;
  font-weight: bold !important;
}

a:hover, input[type="submit"]:hover
{
  text-decoration: underline !important;
}

#newmessages
{
  margin-top: 0 !important;
  border: 1px solid #f55724 !important;
  padding: 5px !important;
  background: #f5e2dc !important;
}

form table.txt2 tbody tr:first-child td
{
  background: #e6e6e6 !important;
}
}
