css:
@namespace url(http://www.w3.org/1999/xhtml);

/* o == verified with original plan.io CSS */
@-moz-document domain("plan.io") {
    #top-menu {
        background-color: #222 !important; /* o */
        height: auto !important;
    }
    #top-menu ul { padding: 0 !important; }
    #top-menu li, #top-menu #loggedas { line-height: 170% !important; }

    #header {
        border-top-width: 0 !important;
        background-color: #000 !important; /* o */
        background-image: none !important;
    }
    #header h1 { margin: 3px 5px !important; }

    #main-menu { margin: auto !important; }    
    #main-menu li a.selected, #main-menu li a.selected:hover { background-color: #fff !important; }
    
    #main-menu li a:hover { background-color: #444 !important; } /* o */
    #main-menu li a:hover, #main-menu li a.selected, #main-menu li a.selected:hover {
        background-image: none !important;
        color: #000 !important;
    }
    
    #main-menu li:last-child a { border-right-width: 0 !important; }
    #main-menu li a { border-left-width: 0 !important; }

    #main { margin: 0 !important; }
    
    h2, .wiki h1 {
        background-color: transparent !important;
        padding-bottom: 0;
    }
    
    #content { width: 78%; }
    
    div.journal h4 {
        background-color: transparent !important;
        border-left-width: 0 !important;
        border-bottom: 1px solid #ddd !important;
        padding: 6px 0 10px 0 !important;
    }
}
