css:
@-moz-document domain("itea40.jp") {

header, section, #single-header-widget, #sidebar, .appDownloadButton,
ins, footer, .dp_related_posts_vertical, .mg45px-top,
span.author, .breadcrumb_arrow, #single-footer-widget
{ display: none !important; }

#container {
  width: 730px !important;
  background-color: #fff;
  margin: 40px auto;
  padding: 0 40px !important;
  border: 0;
}
body {
  background-color: #f2f2f2;
}
.content.right, .content.three-col.right2 {
  border: 1px solid #ddd !important;
}
.content.right:before, .content.three-col.right2:before {
  border-width: 0 !important;
}

}

@-moz-document domain("itea30.jp") {
header, .tabs-outer, footer, .column-right-outer,
  .breadcrumbs, .post-header, div[id^="fb"], div[class^="fb"],
  .post-body>iframe, .post-footer-ads, #zenback, .post-footer,
  .post-outer>iframe, .blog-pager, #navbar, .body-fauxcolumns,
  .appDownloadButton
  { display: none !important; }

  .main-inner .columns {padding:0;}

  .content-outer, .content-fauxcolumn-outer, .region-inner {
    min-width: 760px;
    max-width: 760px;
    _width: 1030px;
  }
  .content-outer { margin: 2em auto !important; }

  body { background-color: #f2f2f2; }

}
