css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.mortigitempo.com") {

body {

   background: white url("http://www.bittbox.com/wp-content/uploads/2007/01/fading_backgrounds/fading_background_8.png") repeat-x fixed !important;

   margin-top: 0px !important ;
}

.border {

   background-color: black !important;

}

.tcat {

   background-color: #669900 !important;

}

.thead {

   background-color: #669900 !important;

   color: white !important;

}

.vbmenu_control {
   
   background-color: white !important;

   color: black !important;

}


a, a:visited {
  
   color: black !important;
  
   text-decoration: underline !important;

}

a:hover, a:active, body_ahover {

   color: #669900 !important;

   text-decoration: none!important;

}

.tcat a, .tcat a:visited {

   color: white !important;

   text-decoration: none !important;

}
 
.thead a:link, .thead_alink, .thead_avisited {

   color: white !important;

   text-decoration: none !important;

}

.thead a:hover, .thead a:active, .thead_ahover {

   color: white !important;

   background-color: #669900 !important;

   text-decoration: underline !important;

}

.navbg a:link, .navbg a:visited, .navbar a:link, .navbar a:visited {

  text-decoration: none !important;

}

}
