css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org") {

html > body {
   width: 1024px !important;
   min-width: 1024px !important;
}

div.ad,
div#social {
   display: none !important;
}

table.author-styles {
   border-collapse: collapse;
   border-spacing: 1px 1px;
   border-style: solid;
   border-color: silver;
}

table.author-styles tr,
table.author-styles td,
table.author-styles th {
   padding: 2px 4px;
   border-style: solid;
   border-color: silver;
   vertical-align: top !important;
}

table.author-styles tr:hover td,
table.author-styles tr:hover th {
   background-color: #e1f6ff;
}

table.author-styles td a,
table.author-styles th a {
   text-decoration: none !important;
}

table.author-styles td a:hover,
table.author-styles th a:hover {
   text-decoration: underline !important;
}

table.author-styles td.date-value,
table.author-styles th.date-value {
   width: 105px !important;
  min-width: 105px !important;
}

table.author-styles tr td:last-child {
   min-width: 135px !important;
}

}
