css:
@-moz-document url-prefix(http://www.qidian.com/) {
	#down_3g_20130621,
	#mainContent + div,
	#qdass_bottom_fixbox
	{
		display: none;
	}

	*{
		font-family: 'yahei'
	}
}

@-moz-document url(http://www.qidian.com/Default.aspx) {
	#div_10022482,
	div.unusual_links,
	div.DH11,
	div.DH21,
	div.DH31,
	div.top_searchbox,
	div.mmweb,
	div.friendly_link
	{
		display: none;
	}
	
	#head{
		height: auto;
		padding-bottom: 10px;
	}

	#head > div.DH01{
		background: none;
		border: 1px solid #ccc;
	}
}

@-moz-document url-prefix('http://www.qidian.com/Book/'){
	#head,
	div.sidecontbox,
	div.intro > div:last-child,
	div.client_nav,
	input.qdtj_btn,
	div.author_tj,
	#allFix,
	#bshd
	{
		display: none;
	}

	#mainContent{
		margin-top: 10px;
	}

	#book_home{
		margin-left: 100px;
	}

	div.bookupdata{
		margin-bottom: 10px;
	}
}

@-moz-document url-prefix('http://read.qidian.com/BookReader/'){
	#qd_reader_header,
	#sidecontbox,
	div.tjbook.al_tjbook,
	#down_3g_20130621,
	#ad_continer_1 + iframe
	{
		display: none!important;
	}

	#bookdirectory .bigcontbox{
		margin-left: -100px;
	}
}

@-moz-document url-prefix('http://me.qidian.com/'){
	#head-bar,
	#ulUserMenu,
	h5.smr-title,
	div.sm-recommendation,
	div.sm-recommendation,
	div.side-list,
	#divEverydayTask,
	div.focus-subject,
	div.interesting-list,
	div.other-service,
	a[href^='http://game'],
	div.space-block,
	#footer > div
	{
		display:none;
	}

	div.top-header{
		margin: auto;
	}
}

@-moz-document url('http://me.qidian.com/'){
	div.post-field,
	div.ad-shell
	{
		display:none;
	}
}

@-moz-document url-prefix('http://me.qidian.com/bookShelf/bookCase.aspx'){
	div.shell_tuijian
	{
		display:none;
	}
}
