css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("feedly.com") {

    #feedlyProBar, #feedlyMessageBar, #searchBox { display: none !important; }
    #timeline img {
        height: auto !important;
        max-width: 100% !important;
        width: auto !important;
    }
    
    /* Backup for wide and clean modifications */
    .entryBody pre { 
        font-family: courier new;
        font-size: 11px;
        line-height: normal;
    }
    .entryBody th { 
        text-align: right;
        padding-right: 5px;
    } 
    .entryBody th, .entryBody td { 
      font-family: courier new;
      font-size: 12px;
    }

    .inlineFrame { padding:10px; }
    .frameActionsTop, .headerInfo.headerInfo-article { margin-top:0; }
    .frameActionsTop { height:0; line-height:0; }
    .shareBarHolder, .entryBody table, .entryBody p, #timeline img { margin-bottom:0 !important; margin-top:0 !important; }
    .entryBody h3 { margin-bottom:0; margin-top:0; line-height:0; }
    #timeline img { max-height: 400px; }
    .entryholder { padding-bottom:0; }
    
    .sliderWidth { width: 90%; }
    
    .u4Entry.quicklisted { background: #eef8f0 url("http://movies/giga-star.png") no-repeat scroll right bottom; }

    .u4EntryAnnotationHolder { width: 86px; }
    .u4Entry { margin-bottom: 1rem; }
    .u4Entry .visual { background-size: cover; width: 86px; }
    .u4Entry .marginMaker { margin-left: 106px; }
    .floatingEntryOverlay { background-color: rgba(0, 0, 0, 0.2); }

    #floatingBar h1 .hhint { display: block; padding-left: 0; }
    #floatingBar, .floatingBar { padding: 12px 10px; }
    
    .slided #feedlyPart0 {
        animation: none !important; }
}
