css:
@-moz-document domain("adac.de") {
#overlay {
	background-color : rgb(0,0,0) !important;
	opacity : 0.9 !important;
}
#PopuplayerBox #floatedDivGalleryPopup .sh-floating-box-outer.color-scheme-world ,
#PopuplayerBox #floatedDivGalleryPopup .sh-floating-box-outer.color-scheme-world .sh-floating-box-header ,
#PopuplayerBox #floatedDivGalleryPopup .sh-floating-box-outer.color-scheme-world .sh-floating-box ,
#PopuplayerBox #floatedDivGalleryPopup .sh-floating-box.content.vollbild-box-content ,
#PopuplayerBox #floatedDivGalleryPopup #zoom_image_wrapper
{
	background-color : transparent !important;
}
#PopuplayerBox #floatedDivGalleryPopup .icon-close {
	background-color : #DADADA !important;
	height : 11px !important;
	width : 11px !important;
	border : 1px solid silver;
}
#PopuplayerBox #floatedDivGalleryPopup .icon-close:hover {
	border-color : yellow;
}
#PopuplayerBox .right.close-gallery-popup {
	color : silver !important;
	text-decoration : none !important;
	border-bottom : 1px dotted silver;
}
#PopuplayerBox .right.close-gallery-popup:hover {
	color : yellow !important;
	text-decoration : none !important;
	border-color : yellow;
}
}
