css:
@-moz-document domain("instasync.com") {
p, b, i, u {color: #ff7;}
body {color: #000;}
body.dim, body {background: none; background-color: #000}
body.dim .navbar-default, .room-container .room-component-row, body.dim .footer, .room-container .room-component-row .chat, .navbar-default {background: none; background-color: #333;}
body.dim .room-container .room-component-row .video-controls, .room-container .room-component-row .video-controls .tab-content #tabs_playlist .playlist li:hover, .room-container .room-component-row .video-controls, .page-container, .footer {background-color: #222;}
body.dim .room-container .room-component-row .video-controls .tab-content, .nav-tabs>li.active>a, .nav-tabs>li.active>a:hover, .nav-tabs>li.active>a:focus, .room-container .room-component-row .video-controls .tab-content #tabs_playlist .playlist, .room-container .room-component-row .video-controls .tab-content #tabs_playlist .playlist li {background-color: #444; color: #000}
input {background-color: #111; color: #ff7;}
}
