@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("pinboard.in") {
    a.bookmark_title.unread {
        font-style: italic;
        font-weight: bold;
    }
    
    a.mark_read {
        background-color:#d9d9d9;
        border: 1px solid #c0c0c0;
        font-weight: bold;
        padding: 0 4px;
    }    
}
