css:
@-moz-document url-prefix("http://www.baidu.com/s?"), url-prefix("http://www.baidu.com/baidu?") {
body {
    overflow-x: hidden !important;
}

#container {
    width: 1280px !important;
}

#content_right {
    display: none;
}

#content_left {
    display: block !important;
    width: 100% !important;
}

#content_left > .result, #content_left > .result-op, #content_left > .c-container {
    float: left !important;
    display: block !important;
    vertical-align: text-top !important;
    width: 50% !important;
    overflow: hidden !important;
    z-index: 0 !important;
    position: relative !important;
    border-top: 2px solid #dddddd !important;
    padding-top: 2px !important;
}

#content_left > .result:hover, #content_left > .result-op:hover, #content_left > .c-container:hover {
    opacity: 0.90 !important;
    overflow: visible !important;
    z-index: 1 !important;
    border-top: 2px solid #bbbbbb !important;
}
}
