css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("deviantart.com") {
div#top-55 {position:fixed!important;background-color:transparent!important;border:none!important;}
div#rockdock {background-color:#313f3b!important;width:100%!important;left:-17px!important;border-bottom-color:#faa!important;}
div#topLeft-55,
div#topRight-55 {display:none!important;background-image:url('http://i282.photobucket.com/albums/kk280/Yeffsie/Design%20Images/sleek.gif')!important;background-color:transparent!important;}
span#exitlinker a#exitlink {background-image:none!important;margin-left:5px!important;}
span#exitlinker {float:right!important;}
div#userbarDropdownStoptouch,
div.popup2-click-friends {position:fixed!important;}
img.popup2-shadow {display:none!important;}
div#userbarDropdownShadow {display:none!important;}
a.dockrocker {display:none!important;}
div#rockdock {background-color:black!important;}
span#menuDropArrow {background-image:url('http://i282.photobucket.com/albums/kk280/Yeffsie/Design%20Images/sleekpink.gif') !important;background-position:0px 0px !important;}
span#menuDropArrow:hover {background-position:0px -15px !important;}
span#menuDropArrow:active,
span#menuDropArrow:focus {background-position:0px -30px !important;}
span#rockdock-message-count a {color:#faa!important;}
span#rockdock-message-count a:hover {color:#fcc!important;}
a#subbylinker {color:#ccc!important;}
a#subbylinker:hover {color:#fff!important;}
span.userlink {color:#faa!important;}
span.userlink a {color:#faa!important;}
span.userlink a:hover {color:#fcc!important;}

}
@-moz-document url-prefix("http://chat.deviantart.com/chat/") {
div#rockdock {left:0px!important;}
}
