@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("pcinpact.com") {
* {
  color: black !important;
  border: black !important;
  background: none !important;
  box-shadow: none !important;
  text-shadow: none !important;
}
div#prixdunet, div#bloc_jauge_abonnement {
  visibility: hidden !important;
}
div#bloc_commentaires, div#bloc_actu_abonnez, div#actu_auteur, div#actu_social, img.reduced_dedicated, div.box-shadow-blocTlf, span.forfaits, div#bloc_price_tlf {
  display: none !important;
}
#actu_content img {
  opacity: 0.2 !important;
}
#actu_content img:hover, #list_actu img {
  opacity: 0.5 !important;
}
}
