css:
@-moz-document url-prefix("http://theoldreader.com/posts/all"), url-prefix("http://www.theoldreader.com/posts/all") {
* {
    font-weight: normal !important;
}
#sidebar,
.subscribe-fixed-top,
.page-header,
.has-tooltip,
.navbar
{
    display: none !important;
}
.reader .container-fluid {
    top: 0px;
}
.slide {
	top: 36px !important;
}
.nano.body-fixed-top.has-scrollbar {
    width: 99%;
    left: 0;
    margin-left: 1%;
}
.label.label-transparent {
    display: none;
}
.label.label-transparent.pull-right {
    display: inline-block;
}
.post.listview .header .name {
    width: 150px;
}
.post .pull-left.span8 span {
    display: none;
}
}
