css:
@-moz-document domain("pastebin.com") {
.content_right_menu:nth-child(2) {
  display: none;
}

#monster_frame {
  box-shadow: none;
  border-radius: 0px;
  border: 2px solid white;
}

textarea {
  
  outline: none;
}


.paste_box_icon, .tweet, #code_buttons, .span_left, #logo, #header_middle iframe, 
.i_tf, .search_button, #header_middle, .i_t, .i_n, #header + div + div {
  display: none;
}
  
  
.search_input {
  display: block;
  position: absolute;
}
}
