css:
@-moz-document domain(zen.myatos.net) {

#app_body {
  background: url('http://i.imgur.com/MjvVm9u.jpg') #40a0c0;
  background-repeat: no-repeat;
  background-size: 100% 100%;
}

body .main .main_content {
  background: transparent;
}

.items > li {
  background: white;
  box-shadow: 0 0 20px black;
  box-sizing: border-box;
  margin: 1em;
  padding: 1em;
}
  
ul.filters, .widgets>li, .style_widgets > li, .content {
  background: rgba(255,255,255,0.75);
  padding: 0.5em;
  box-sizing: border-box;
}
  
  .tabs {
      background: none;
    }

.tabs > h2, .ellipsis {
    vertical-align: middle;
  }
  
  .content_footer {
      color: gray;
    }
}
