css:
/*百度百科*/
@-moz-document domain("baike.baidu.com") {
.para {  font-size:16px !important; }
}

/*新浪微博*/
@-moz-document domain("weibo.com") {
* {font-family: "arial narrow","宋体" !important; }
.WB_feed .WB_text {font-size: 16px !important;line-height: 1.5em !important;}
.WB_feed .news dd .list li { font-size: 14px !important;width:100% !important; }
}

/*高校人才网*/
@-moz-document domain("gaoxiaojob.com") {
.article_ad,.ht10,.new-ad950,.new-ad720 { display: none !important; }
.navbar { height:auto !important; }
.viewbox .content p { font-size: 16px !important ; }
}

/*知乎*/
@-moz-document domain("zhihu.com") {
body,
.zm-editable-content,
.question_link,
.zh-summary {
    font-size: 16px !important;
}
#zh-single-question-page,
#zh-single-answer-page,
.zg-wrap {
    width: 1200px !important;
}
.zh-backtotop {
    margin-left: 290px !important;
}
}

/*滴答清单*/
@-moz-document domain("dida365.com") {
* { font-family: verdana ,宋体 !important; }
#task-new { font-size: 13px !important; }
}

/*煎蛋*/
@-moz-document domain("jandan.net") {
h2 { font-size: 16px !important; }
#content .post p {
    font-family:Tahoma,宋体 !important;
    font-size: 16px !important;
  }
#adsense,#homead { display:none !important; }
}

/*IT之家*/
@-moz-document domain("www.ithome.com") {
.content .post_content p {
      font-size: 16px !important;
      font-family: "Arial Narrow",宋体 !important;
    }
}

/*驱动之家*/
@-moz-document domain("news.mydrivers.com") {
.news_info p {
    font-size: 16px !important;
    font-family: "Arial Narrow",宋体 !important;
  }
}

/*cnbeta.com*/
@-moz-document domain("cnbeta.com") {
.article .body .content p ,
.article .body .introduction p {
    font-size: 16px !important;
    font-family: "Arial Narrow",宋体 !important;
  }
}
