css:
@-moz-document url("http://topclassactions.com/lawsuit-settlements/open-lawsuit-settlements"), url("http://www.topclassactions.com/lawsuit-settlements/open-lawsuit-settlements") {
.category, .pagination {
  margin-top: 30px;
}
  
.category tr {
  height: 25px;
}

.category td {
  padding: 5px;
}
  
.list-title {
  font-size: 16px;
  width: 700px;
  font-family: Optima, Segoe, "Segoe UI", Candara, Calibri, Arial, sans-serif;
}
  
a {
  font-family: Optima, Segoe, "Segoe UI", Candara, Calibri, Arial, sans-serif;
}

.list-date {
  font-size: 12px;
}

#center a.pagenav {
  font-size: 18px;
}
    
#left, #banner, #system-message-container, .contentdescription {
  display: none;
}
    
#center {
  width: 100%;
  margin: 0;
}
}
