css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("photoshopcontest.com") {
body, div.toplinks {background-color: black !important; border-color: black !important }
div.base, td, ul, li {background-color: black !important ; border-color: #070707!important }
div.topnav ul,div.topnav li,div.topnav a,div.topnav {background-color: #4d4d4d !important; border-color: #4d4d4d !important; color: white !important }
div.header center, div.header td, div.header a, div.header input.textbox { background-color: white !important }
td.adv-cal-normal, td.adv-cal-normal center {background-color: #111111 !important; border-color: #222222 !important  }
a:link {background-color: #090909 !important; color: #555555 !important }
a:visited {background-color: #090909 !important; color:#8E5909 !important}
a:hover {background-color: #090909 !important; color: #B65505 !important}
font {color: #F99540 !important}
input.button {color: #B65505 !important}
p, div.base {color: #555555 !important}
td {color: #999999 !important}
i {color: #8E5909 !important}
textarea.textbox, input.button, input.textbox, select, input, textarea {background-color: #333333 !important; border-color: #555555 !important; color: #888888 !important}
div.chatbody {background-color: #080808 !important; border-color: #111111 !important }
div.catrow, div.forumrow, div.topics, table.topics, p.info, div.posts, div.postbody, div.text {background-color: #080808 !important; border-color: #111111 !important }
div.quote2 {background-color: #111111 !important; border-color: #111111 !important }
td.adv-cal-posted {background-color: black !important; border-color: #222222 !important  }
td.adv-cal-dark {background-color: #444444 !important; border-color: #222222 !important   }
div.header, div.header center, div.header center table tbody tr td, div.header center table tbody tr td a {background-color: transparent !important	}
div.header {background-image: url(http://i226.photobucket.com/albums/dd147/pakimo_2/header_bak.jpg) !important}


}
