css:
@-moz-document url-prefix("https://delicious.com/") {
::selection {
  background: rgba(39,143,247,.5)
}

.sidebar,
.site-nav-search input {
  background: #414141;
}
    
.site-nav-search input::-webkit-input-placeholder {
  color: #c1c1c1;
}

.site-nav-search input:-moz-placeholder {
  color: #c1c1c1;
}
      
.site-nav-links li.on a,
body {
  backgorund: #fafafa !important;
  background-color: #fafafa !important;
}

.action-link {
  color: #fafafa !important;
}
  
.item:hover .action-link {
  color: #999 !important;
}
      
.item .domain {
  position: absolute;
  top: 4.7em;
  right: 1.5em;
}

.item .title-wrapper {
  padding: .5em 0 2em;
}
    
.item .note {
  position: absolute;
  top: 4.2em;
}

}
