css:
@-moz-document url-prefix("http://getpocket.com/") {
.page-reader #PKT_header {
  display: none;
}

.page-reader #page .pkt-nav {
  position: relative !important;
  top: -.5em;
  box-shadow: none;
  background: #fafafa;
  height: 40px !important;
}

body.page-reader {
  background: #fefefe !important;
}

.page-reader .toolbar_reader.navigationItem.wrapper.active {
  position: relative;
  bottom: .8em;
}

.page-reader #page_reader.articleview {
  padding-top: 2.1em;
}

.page-reader .text_body blockquote {
  font-style: normal;
}

.page-reader strong, .page-reader b {
  background: rgba(242,242,111,.64);
  font-weight: normal;
}
}
