@-moz-document domain("stri.ms") {

.content-row .img-thumb { 
height: 104px;
width: 130px;
}
body {
background: url(http://i.imgur.com/XE6BLOc.png);
}

.container {
margin-right: 0;
margin-left: 0;
width: 100%;
}

.span2 {
margin-bottom: 20px;
margin-right: 5px;
margin-left: 5px;
min-height: 1px;
background: #f5f5f5;
padding: 19px;
-webkit-border-radius: 4px;
border-radius: 4px;
border: 1px solid #e3e3e3;
}
.content-row {
background: #f5f5f5;
padding: 11px;
border: 1px solid #e3e3e3;
-webkit-border-radius: 4px;
border-radius: 4px;
}
.span2 ul li {
color: #000;
padding: 3px 0 3px 5px;
background: #f5f5f5;
margin: 3px 0 3px 0;
-webkit-border-radius: 4px;
border-radius: 4px;
}
