css:
@-moz-document domain("spapps.co"), domain("play.spotify.com") {
#now-playing-widgets {
  display:none;
}

#now-playing {
  background: #2C2C2D;
}
#radio-star {
  display: none;
}
#thumb-container {
	padding-left: 50px;
}
.top-list {
    background: #2C2C2D;
	border: 0;
    display: none;
}
.top-list > div {
    display: none;
}
.top-list > h2 {
    display: none;
}
#radio-seed {
	display: none;
}
#radio-track-info {
	width: 100%;
	text-align: center;  
    padding-top: 50px;
}
#playing-info {
  width: 100%;
  margin-right: 47%;
}
#playing-info div {
  	font-size: 200%;
	font-weight: 100;
	overflow: auto;
	white-space: normal;
}
}
