css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
    url-prefix("https://www.google.com.au/search"),
    url-prefix("https://www.google.com.hk/search"),
    url-prefix("https://www.google.com/search") {
        
        /* ******** typography ******** */
        
        body {
            font-family: "Helvetica Neue", "Hiragino Sans GB", "Hiragino Kaku Gothic ProN", sans-serif;
        }
        
        .g {
            margin-bottom: 21px!important;
        }
        
        .r {
            font-weight: bold;
            font-size: 16px !important;
            line-height: 24px !important;
        }
        
        .w {
            position: absolute;
            left: -30px;
            top: -4px;
        }
        
        .s, .st {
            font-family: "Athelas", "Linux Libertine O" !important;
            line-height: 16px !important;
            margin-top: 4px;
        }
        
        ._Rm {
            font-style: italic;
        }
    
        /* ******** color ******** */
        
        .r a, .gl a, .osl a, .fl {
            color: #333!important;
        }
        
        .w {
            color: #DD4B39!important;
        }
        
        /* **** others **** */
        
        a, em {
            text-decoration: none!important;
            outline: none;
        }

        .smli, ._kn { display: none }
        #taw { opacity: 0.5 }

}
