css:
/*========================================================
  hackaday.com requid compact
  ========================================================*/
@-moz-document domain(hackaday.com) {
#wrap {width: auto !important; max-width: inherit !important;}
#header {max-width: inherit !important; min-height: inherit !important;}
.header-image #title-area, .header-image #title, .header-image #title a
 {width: auto !important; height: auto !important; text-indent: 0 !important;}
.header-image #title-area {background-size: auto 100%;}
#header .widget-area {width: auto !important; padding: 0 !important;}

#inner {padding: 0 !important;}
#inner .wrap #content
 {width: 100% !important; margin: 0 -300px 0 0 !important; padding: 0 !important;}
#inner .wrap #content>div
 {width: auto !important; margin: 0 300px 0 0 !important; padding: 0 0.5em !important;}
#inner .wrap #sidebar iframe#twitter-widget-0
 {width: 300px !important;}
}
