css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("link.samsung.com") {

    .tableFilelist  td.added{  /* date */
        padding-left:  2px !important;
        text-overflow: clip !important;
    }

    .tableFilelist  td.size {
        padding-left:  2px !important;
        text-overflow: clip !important;
        
        font-size:  88%  !important; padding-right: 0.1em !important;
    }
    .tableFilelist  td.size[title$="KB"] {
        font-size:  90%  !important; padding-right: 0.5em !important;
    }
    .tableFilelist  td.size[title$="MB"] {
        font-size: 100%  !important; padding-right: 1.0em !important;
    }
    .tableFilelist  td.size[title$="GB"] {
        font-size: 110%  !important; padding-right: 1.5em !important;

        font-weight: bold !important;
        
        /* background: yellow  !important; */
    }
}
