css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ps3hax.net") {
/*
    ver 1.01
    - Removes horizontal scroll bar on any screen resolution
    - Hides ad box on top of the page
    - Hides scene status graphics in top
*/

body {
    width: auto !important;
    min-width: 1px !important;
}

body>table {
    display: none !important;
}

body>.doc_header {
    margin-right: 10px !important;
    margin-left: 10px !important;
    width: auto !important;
    min-width: 1px !important;
}

body>#wrapper {
    margin-right: 10px !important;
    margin-left: 10px !important;
    width: auto !important;
    min-width: 1px !important;
}

body>.footer {
    margin-right: 10px !important;
    margin-left: 10px !important;
    width: auto !important;
    min-width: 1px !important;
}

body>#header>.custom_banner {
    display: none !important;
}

/*body>#header>div>a.logo-image>img {
    display: none !important;
}*/

}
