css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.medpagetoday.com") {

#header {
  position: fixed; top: 0; left: 0;
  clip: rect(0, 2mm, 20mm, 0);
  border-left: 2.2mm solid red !important;
  transition: clip .3s .8s linear;
  }
#header:hover {
  clip: rect(0, 1007px, 157px, 0);
  transition-delay: 0s;
  }

/** autohide footer **/
#footer {
  position: relative;
  height: 0;
  }
.thefooter {
  position: absolute; bottom: 0; left: 0;
  height: 20mm;
  clip: rect(0, 2mm, 341px, 0);
  border-left: 2.2mm solid red !important;
  overflow: hidden;
  transition: .3s .8s linear;
  transition-property: clip, height;
  }
.thefooter:hover {
  height: 341px;
  clip: rect(0, 1007px, 341px, 0);
  transition-delay: 0s;
  }

/** no ads **/
#cmeandclinical,
#bannerDiv,
#adDiv,
.footad,

/** no comment **/
.commentsnumber,
.commentCount,
.comments,

/** no social **/
/*.socialbox.tablet,*/
.socialButtons,
.epsLinks > #email,
.twitterByline,
.footconnect,

/** no syndicated "content" **/
.sponsortitle,

.\. {display:none!important}

}
