css:
@-moz-document domain("straitstimes.com") {
.rip_none,
#leaderboard,
#stWrap,
#util,
.purchase,
#navSuppWrap,
#footer,
.twintower-ad-right,
.panelTopOtherStories,
.storyLeft,
.byline
{
    display: none;
}
.story_main {
    width: 600px !important;
}
.custom-story-right {
    width: 330px !important;
}
.storyRight {
    float: none;
    width: 100%;
}
.story h1 {
    font-size: 18px;
}
.mp td,
.otitle {
    font-size: 10px !important;
}
.mp td {
    line-height: 1.2em;
}
.view .view-content > div:nth-child(n+11) {
    display: none;
}
}
