css:
@-moz-document domain("twitter.com") {
  .wrapper,
  .wrapper-narrow,
  .wrapper-permalink {
    margin: 0 auto 0 0 !important;
    width: 1352px !important;
  }

  .content-main {
    width: 1044px !important;
  }


  .js-stream-item {
    margin: 0 !important;
    width: 520px !important;
    -webkit-transition: margin 0.5s ease-in-out;
    -moz-transition: margin 0.5s ease-in-out;
    -o-transition: margin 0.5s ease-in-out;
    -ms-transition: margin 0.5s ease-in-out;
    transition: margin 0.5s ease-in-out;
  }

  .js-has-navigable-stream,
  .fully-expanded-conversation,
  .js-activity-favorite,
  .js-activity-retweet {
    margin: 0 0 0 522px !important;
    -webkit-transition: margin 0.5s ease-in;
    -moz-transition: margin 0.5s ease-in;
    -o-transition: margin 0.5s ease-in;
    -ms-transition: margin 0.5s ease-in;
    transition: margin 0.5s ease-in;
  }

  .open {
    margin: 0 0 0 261px !important;
    -webkit-transition: margin 0.5s ease-out;
    -moz-transition: margin 0.5s ease-out;
    -o-transition: margin 0.5s ease-out;
    -ms-transition: margin 0.5s ease-out;
    transition: margin 0.5s ease-out;
  }

  .profile-page-header {
    margin-right: 522px !important;
  }
}
