css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("https://twitter.com/"), url("http://twitter.com/"), url("https://twitter.com"), url("http://twitter.com") {

.tweet-actions {
    visibility: hidden;
    }

.tweet:hover .tweet-actions, .open .tweet-actions, .permalink-tweet .tweet-actions {
    visibility: visible !important;
    }

.tweet .expanded-content, .tweet .cards-media-container, .tweet .js-media-container, .js-permalink .tweet-content {
    display: none !important;
    }
    
.open .expanded-content, .open .cards-media-container, .open .js-media-container, 
.permalink-tweet .expanded-content, .permalink-tweet .cards-media-container, 
.permalink-tweet .js-media-containerm, .opened-tweet .js-permalink .tweet-content  {
    display: inline !important;
    }

}
