css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com") {
.rtl #suggested-users { clear: left !important;} 
.profile-modal .stream-item-header .avatar, .profile-modal .permalink-header .avatar{    margin-right: -50px !important;} 
.tweet-stats-container .stats .avatar { border-color: #FFFFFF; } 
.stream-item-header .avatar, .permalink-header .avatar {    margin-right: -62px !important;} 
.tweet-text {font-family: tahoma !important; font-size: 13px;} 
.favorited a.profile-picture, .favorited .avatar { border: 3px solid #FF9B00;}
.favorited ._timestamp { color: #FF9B00;} 
h3,.nav > li > a .text,h2 {    font-family: b yekan; } 
.stream-item-header {    padding: 0 0 5px; } 
.profile-card.profile-header .stats a strong {    font-family: b yekan; }
.btn {    font-family: b yekan;    font-size: 13px;    font-weight: normal !important;} 
.modal-header .modal-title {    font-size: 13px;} 
.tweet .time {    float: left !important;}
a.profile-picture ,  .avatar { border: 3px solid rgba(0, 0, 0, 0);    border-radius: 50px !important;} 
.profile-picture .avatar {    border: 0 none;} 
.stream-item:hover  ul.tweet-actions li {    display: inline  !important;} 
.stream-item  ul.tweet-actions li{    display: none !important;}
}
