css:
@-moz-document domain("twitter.com") {
.stream-container .tweet .tweet-actions {
  display: none;
}

.stream-container .tweet:hover .tweet-actions {
  display: inline-block !important;
  color: #999 !important;
}

.stream-container .tweet:hover .tweet-actions a, .stream-container .tweet:hover .tweet-actions .more-tweet-actions b {
  color: #999 !important;
}
      
.stream-container .tweet:hover .tweet-actions .icon {
  background-color: #999 !important;
}

}
