css:
@-moz-document domain(eow.alc.co.jp) {

body { color: #333; background: #fff; }
body,
#AreaUpperLeft { font: normal normal 15px/1.7 "Segoe UI", sans-serif; }

div,
#AreaUpperLeft #gnav,
#AreaUpperLeft #tabhead,
#resultsArea > table,
#AreaUpperLeft #introduceEowp,
#AreaUpperLeft #introduceEowp ~ div,
#AreaUpperLeft .pro_btn { display: none; }

#AreaBody,
#AreaUpperLeft,
#AreaUpperLeft div { display: block; }

div[style*='display'] { display: none !important; }

}



@-moz-document domain(www.alc.co.jp) {

div,
.tab-handle,
.eijiro-pro { display: none; }

#content,
.main-content,
#search,
#search div { display: block; }

}
