css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {
    /* navigation bar */
    #headNav {
    	width: 455px;
    	margin-top: -26px;
    }
    #navSearch .uiSearchInput input {
        width: 150px!important;
    }
    #navSearch {
    width: 185px!important;
    }
    #pageHead {
        float: left;
    }

    /* content */
    #pagelet_home_stream{
        width:400px;
    }
    .storyInnerContent {
        margin-left: 4px!important;
    }
    .uiStreamMessage{
        margin-left:64px!important;
        min-height:33px;
    }
    .actorPhoto {
        margin-left: 10px !important;
        margin-top: 21px !important;
    }
    .uiStreamAttachments{
        padding-left:5px;
    }
    #pagelet_composer{
        margin-left:-8px!important;
        margin-right:110px;
    }
    .homeWiderContent .uiStreamStory {
        padding-left: 0px!important;
    }

    /* remove right bar */
    #pagelet_rhc_footer,#pagelet_ego_pane,#pagelet_ego_contextual_group{
        display:none;
    }
    UL.uiList.mbm{
        display:none;
    }
    #pagelet_group_composer.groupComposerCleanWrap{
        margin-top:20px;
    }
     .groupsSkyDescription {
        height: 30px!important;
    }
    
    /* but preserve right bar birthday notify*/
    #rightCol {
        float: left!important;
        padding-left: 250px;
        margin-bottom:-60px!important;

    }
    #pagelet_reminders > div:nth-child(1) > div:nth-child(1) > div:nth-child(1){
        display:none;
    }
    #birthday_reminders_link{
        margin-top:-19px;
    }

}
