@-moz-document domain("ask.fm") {
 ::-webkit-scrollbar {
 	/* Width */
 	width: 1.2em!important;
 }

 /* Color of handle ___________________________________________________________________________*/

 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track ______________________________________________________________________________*/

 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom ___________________________________________________________*/

 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }
}

@-moz-document domain("ask.fm") {
/*__________________________________________________________________________________*/

* {
	text-decoration: none!important;
}

selector {
	text-transform: capitalize!important;
}

.answerForm .answer,
.askForm .question,
.askfm-ad-slot:empty+.askfm-ad-slot:empty+.streamItem-notification::before,
.atcomplete .item,
.atcomplete,
.btn-circle-following,
.defaultListItem,
.hashtags-input,
.inputFormPadding,
.item-page+.item-page .streamItem-notification:first-child::before,
.mobileMenu .siteLinks a,
.photopoll .choices .vote,
.scomplete .item,
.scomplete,
.search-input,
.simpleFormPadding,
.socialListItem,
.streamItem-notification+.streamItem-notification::before,
.subTabBar,
.subTabBar-transparent,
.tabBar a,
.tagcomplete .item,
.tagcomplete,
:focus,
body.theme-2 .askfm-ad-slot:before,
body.theme-2 .atcomplete:before,
body.theme-2 .avatar-thumb.loader:before,
body.theme-2 .background-thumb.loader:before,
body.theme-2 .item-page-next.loader:before,
body.theme-2 .item-page-prev.loader:before,
body.theme-2 .playIcon-gif.loader:before,
body.theme-2 .playIcon-youtube.loader:before,
body.theme-2 .streamItem-pymk.loader:before,
body.theme-2 .streamItem-pyml.loader:before,
body.theme-2 .tagcomplete:before,
body.theme-2 .upload-file+label.loader:before,
body.theme-2 .vs.loader:before {
	border: none!important;
	outline: none!important;
	box-shadow: none!important;
}

/*___ Fonts ________________________________________________________________________*/

.social-links a {
	font-size: 31px!important;
}

.menuBar>a {
	font-size: 31px!important;
}

.btn-circle-following {
	font-size: 27px!important;
}

selector {
	font-size: 21px!important;
}

.streamItem_content {
	font-size: 19px!important;
}

.pushPromo_box p,
.streamItem_meta,
.subTabBar a,
.subTabBar-transparent a,
body,
footer,
input,
textarea {
	font-size: 17px!important;
}

.simpleFormLabel,
.streamItem_threadDetails
{
	font-size: 15px!important;
}

.streamItem_content {
	line-height: 25px!important;
}

/*___ Remove Items _________________________________________________________________*/

selector {
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/*___ Left/Right/Top/Bottom ________________________________________________________*/

.photopoll .choices.choice-1 .vote {
	left: 4px!important;
}

.photopoll .choices.choice-2 .vote {
	right: -30px!important;
}

footer .social-links .icon-facebook::before,
footer .social-links .icon-twitter::before,
footer .social-links .icon-vkontakte::before
{
	padding-left: 56px!important;
}

/*___ Margins ______________________________________________________________________*/

.streamItem,
.twoColumnBody .main-column,
.twoColumnBody .side-column
{
	margin: 12px!important;
}

selector {
	margin: 3rem!important;
}

/*___ Paddings _____________________________________________________________________*/

.streamItem,
.tagcomplete,
.twoColumnBody .main-column,
.twoColumnBody .side-column
{
	padding: 12px!important;
}

selector {
	padding: 6px!important;
}

/*___ Heights ______________________________________________________________________*/

.streamItem_visual a,
.streamItem_visual iframe,
.streamItem_visual img,
.streamItem_visual video
{
	max-height: none!important;
}

selector {
	height: auto!important;
}

.menuBar>a {
	height: 59px!important;
}

/*___ Widths _______________________________________________________________________*/

.twoColumnBody .rsp-container {
	max-width: none!important;
}

.rsp-container {
	width: 98%!important;
}

.twoColumnBody .main-column {
	width: 80%!important;
}

.optionsBar-option.icon-facebook,
.optionsBar-option.icon-twitter,
.optionsBar-option.icon-vkontakte {
	width: 49px!important;
}
}

@-moz-document domain("ask.fm") {
/*__________________________________________________________________________________*/

a[href],
td span {
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

.tagcomplete .tag mark,
a[href]:visited {
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

.tagcomplete .item:hover .count,
a[href]:hover span,
a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label {
	color: #ff0!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

.pageTitle.answer,
.pageTitle.icon-ask-question,
.pageTitle.icon-ask-question-stroke,
.pageTitle.icon-photopoll,
.pageTitle.icon-shoutout-stroke,
.streamItem_content,
.tagcomplete .count,
body.theme-2 #deleteAccount p,
body.theme-2 .btn-circle-following,
body.theme-2 .footerBlock span,
body.theme-2 .streamItem-notification strong.user,
body.theme-2 .streamItem_origin-promoted,
body.theme-2 .streamItem_participants,
body.theme-2 .streamItem_threadDetails span,
body.theme-2 .upload-file+label,
body.theme-2 a,
body.theme-2 a.wallet_footer span,
li,
p {
	color: #fff!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Khaki ________________________________________________________________________*/

.userName,
body.theme-2 .popover a,
body.theme-2 .socialListItem,
h1,
h2 {
	color: #F0E890!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Burlywood ____________________________________________________________________*/

.userName+.userName,
h3,
h4 {
	color: #e0b880!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Sandy Brown __________________________________________________________________*/

h5,
h6,
label {
	color: #F0A060!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Blue _________________________________________________________________________*/

time {
	color: #07beff!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Green ________________________________________________________________________*/

selector {
	color: #07ff24!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Red __________________________________________________________________________*/

selector {
	color: #ff0707!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea {
	background-color: #1d0909!important;
	border-radius: 22px!important;
	color: #fff!important;
	padding: 12px!important;
}

/*__________________________________________________________________________________*/

::selection {
	background: #ff9!important;
	color: #000!important;
}

pre ::selection {
	background: #ff9!important;
	color: #fff!important;
}

::placeholder {
	/* Chrome, Firefox, Opera, Safari 10.1+ */
	color: #f48f8f;
	opacity: 1;
	/* Firefox */
}

}

@-moz-document domain("ask.fm") {
/*__________________________________________________________________________________*/

body,
html {
	background: url(https://image.ibb.co/hVmiOe/wallpaper_with_black_background_45_free_wallpaper.jpg)!important;
	background-attachment: fixed!important;
}

/*___ Background Gray ______________________________________________________________*/

.topMenu,
.welcome-9 .topBar,
.welcome-container footer,
h3.strikeThrough span {
	background-color: #060505!important;
}

selector {
	background-color: #231c1c!important;
}

/*___ Background 0000 ______________________________________________________________*/

.askForm .question,
.emptyResults,
.inputFormPadding,
.loginServices a.loginService.icon-facebook,
.loginServices a.loginService.icon-instagram,
.loginServices a.loginService.icon-twitter,
.loginServices a.loginService.icon-vkontakte,
.nullState,
.pushPromo_wrapper,
.simpleFormPadding,
.socialListItem,
.subTabBar_wrapper,
.tabBar,
.tagcomplete .count {
	background: rgba(0, 0, 0, 0)!important;
}

/*___ Background-color 0000 ________________________________________________________*/

.atcomplete .item,
.atcomplete,
.coins_container .coins,
.photopoll .choices .vote,
.scomplete .item,
.scomplete,
.searchBar,
.streamPanel,
.streamPanel-toggle,
.streamUsers-narrow,
.subTabBar,
.subTabBar-transparent,
.tagcomplete .item,
.tagcomplete .tag mark,
.topBar .coins,
body.theme-2 .topMenu .btn-action-icon,
body.theme-2 .topMenu .icon-create-content {
	background-color: rgba(0, 0, 0, 0)!important;
}

/*__ Blue __________________________________________________________________________*/

.btn-secondary.transparent,
.welcome-container .btn-primary-wide.inverse {
	background-image: linear-gradient(to bottom right, #043dda, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

.btn-secondary.transparent:hover,
.welcome-container .btn-primary-wide.inverse:hover {
	background-image: linear-gradient(to bottom right, #154eeb, #000)!important;
	color: #ff0!important;
}

.btn-secondary.transparent:active,
.welcome-container .btn-primary-wide.inverse:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*__ Red __________________________________________________________________________*/

body.theme-2 .btn-primary[value="Cancel"] {
	background-image: linear-gradient(to bottom right, #f00, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

body.theme-2 .btn-primary[value="Cancel"]:hover {
	background-image: linear-gradient(to bottom right, #f11, #000)!important;
	color: #ff0!important;
}

body.theme-2 .btn-primary[value="Cancel"]:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*__ Orange ________________________________________________________________________*/

.btn-primary-wide,
.welcome-container .btn-primary-wide {
	background-image: linear-gradient(to bottom right, #da3a04, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

.btn-primary-wide:hover,
.welcome-container .btn-primary-wide:hover {
	background-image: linear-gradient(to bottom right, #eb4b15, #000)!important;
	color: #ff0!important;
}

.btn-primary-wide:active,
.welcome-container .btn-primary-wide:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*___ Green ________________________________________________________________________*/

body.theme-2 .btn-primary,
body.theme-2 .btn-primary-icon,
body.theme-2 .btn-primary-wide {
	background-image: linear-gradient(to bottom right, #81BC2E, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

body.theme-2 .btn-primary-icon:hover,
body.theme-2 .btn-primary-wide:hover,
body.theme-2 .btn-primary:hover {
	background-image: linear-gradient(to bottom right, #92cd3f, #000)!important;
	color: #ff0!important;
}

body.theme-2 .btn-primary-icon:active,
body.theme-2 .btn-primary-wide:active,
body.theme-2 .btn-primary:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*___ Plain Black __________________________________________________________________*/

.twoColumnBody .side-column,
.twoColumnBody .main-column {
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

.twoColumnBody .side-column:hover,
.twoColumnBody .main-column:hover {
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

/*___ Plain ________________________________________________________________________*/

.streamItem,
.tagcomplete,
body.dialogBody .main-column {
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

.streamItem:hover,
.tagcomplete:hover,
body.dialogBody .main-column:hover {
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

/*___ Red Gradient _________________________________________________________________*/

.atcomplete .item.active,
.atcomplete .item:hover,
.scomplete .item.active,
.scomplete .item:hover,
.tagcomplete .item.active,
.tagcomplete .item:hover {
	background: linear-gradient(to bottom, #300202 0%, #590505 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
}

/*__ Blue Gradient _________________________________________________________________*/

.pushPromo_box {
	background: linear-gradient(to bottom, #03193c, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
}

/*__ Green Gradient ________________________________________________________________*/

body.theme-2 .subTabBar a.active,
body.theme-2 .subTabBar-transparent a.active,
body.theme-2 .tabBar a.active {
	background: linear-gradient(to bottom, #15534d 0%, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	text-shadow: 1px 1px #555!important;
}

/*__ Yellow Gradient _______________________________________________________________*/

selector {
	background: linear-gradient(to bottom, #9d9d5d 0%, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	text-shadow: 1px 1px #555!important;
}

/*__ Orange Gradient _______________________________________________________________*/

selector {
	background: linear-gradient(to bottom, #da3a04 0%, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	text-shadow: 1px 1px #555!important;
}
}

@-moz-document domain("ask.fm") {
/*__________________________________________________________________________________*/

.loginServices a.loginService,
.socialListItem,
.streamItem,
.streamUsers-narrow .userItem,
.tagcomplete .item,
.welcome-container footer .faces a
{
	filter: brightness(75%)!important;
}

/*__________________________________________________________________________________*/

.loginServices a.loginService:hover,
.socialListItem:hover,
.streamItem:hover,
.streamUsers-narrow .userItem:hover,
.tagcomplete .item:hover,
.welcome-container footer .faces a:hover
{
	filter: brightness(100%)!important;
}

/*__________________________________________________________________________________*/

img {
	border-radius: 22px!important;
}

/*__________________________________________________________________________________*/

img {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

/*__________________________________________________________________________________*/


li,
p,
tr {
	filter: brightness(68%)!important;
	transition: color .125s ease-in, color .5s ease-out, filter .5s ease-in, filter .8s ease-out, box-shadow .125s ease-in, box-shadow .5s ease-out!important;
}


li:hover,
p:hover,
selector:hover p,
tr:hover {
	filter: brightness(100%)!important;
	transition: color .125s ease-in, color .5s ease-out, filter .5s ease-in, filter .8s ease-out, box-shadow .125s ease-in, box-shadow .5s ease-out!important;
}

/*__________________________________________________________________________________*/

selector {
	opacity: .8!important;
}

selector:hover {
	opacity: 1!important;
}
}

