css:
@-moz-document domain("twitter.com") {
  .js-media-container .cards-base {
    margin: 0;
  }

  .opened-tweet .js-media-container .cards-base {
    margin: inherit;
  }

  a.media-thumbnail .is-preview {
    display: none;
  }
          
  .opened-tweet a.media-thumbnail .is-preview {
    display: block;
  }
}
