css:
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");
@-moz-document url("chrome://browser/content/browser.xul") {
  #titlebar-buttonbox .titlebar-button {
    -moz-appearance: none !important;
    width: 14px !important;
    height: 14px !important;
    margin: 0 3px !important;
    border-radius: 12px !important;
    border-style: inset !important;
    list-style-image: none !important;
    cursor: pointer;
  }
  #titlebar-buttonbox-container { margin: .25em .5em -.25em 0 !important }
  window[sizemode="maximized"] #titlebar-buttonbox-container {
    margin: .5em .5em -.5em 0 !important
  }
  #titlebar-min   {
    background: radial-gradient(ellipse at bottom, #fe0 50%, #f60 100%) !important;
    border-color: #f60 !important;
  }
  #titlebar-max   {
    background: radial-gradient(ellipse at bottom, #af0 50%, #b80 100%) !important;
    border-color: #080 !important;
  }
  #titlebar-close {
    background: radial-gradient(ellipse at bottom, #f00 50%, #800 100%) !important;
    border-color: #c00 !important;
  }
  #titlebar-min:hover   {
    background: radial-gradient(ellipse at center, #fe0 50%, #f60 100%) !important;
    border-color: #fe0 !important;
  }
  #titlebar-max:hover   {
    background: radial-gradient(ellipse at center, #af0 50%, #b80 100%) !important;
    border-color: #af0 !important;
  }
  #titlebar-close:hover {
    background: radial-gradient(ellipse at center, #f00 50%, #800 100%) !important;
    border-color: #f00 !important;
  }
  #appmenu-button { height: 21px !important; }
  window[sizemode="maximized"] #appmenu-button { height: 20px !important; }
  window[sizemode="maximized"] #toolbar-menubar[autohide="false"] + #TabsToolbar #titlebar-placeholder-on-TabsToolbar-for-captions-buttons,
  window[sizemode="normal"] #titlebar-placeholder-on-TabsToolbar-for-captions-buttons {
    display: none;
  }
}
