css:
@-moz-document url-prefix("http://huaban.com/design") {
/*头部*/
#design_header {
background: #FAFAFA;
border: 1px solid rgba(0,0,0,0.05);
border-radius: 0;
box-shadow: inset 0 1px rgba(255,255,255,0.6);
margin-top: 26px;
position: relative;
background-clip: padding-box;
}
#design_header .tabs .tab {
border-right: 0 none;
padding: 0 20px;
}
#design_header .tabs {

border-left: 1px solid #ededed;

}
/*--搜索框*/
.clear-input {
background: #FCFCFC;
border: 1px solid #ddd;
border-radius: 2px;
box-shadow: inset 0 1px 2px rgba(0,0,0,0);
}


#design_header .tabs .focus {
color: #383838;
background: #f2f2f2;
box-shadow: inset 0 1px 0 rgba(255,255,255,0.5);
}
#design_header .tabs .focus:before {
content: "";
display: block;
position: absolute;
left: -1px;
right: -1px;
top: -1px;
border-top:0 none;
}
#design_header .tabs .focus:after {
content: "";
display: block !important;
position: absolute;
left: -1px;
right: -1px;
bottom: -1px;
border-top: 1px solid #ea714c;
}

.design-pages .featured-stuff .boards .board-item {
height: 202px;
border-bottom: 2px solid #EDEDED;
background: none transparent;
}
.design-pages .featured-stuff .boards .board-item .board-title {
color: #444;
left:0px;
}

.design-pages .featured-stuff .boards .board-item .board-title {
left:0;
}

}
