css:
@-moz-document url-prefix("http://tumblr.com"), url-prefix("https://tumblr.com"), url-prefix("http://www.tumblr.com"), url-prefix("https://www.tumblr.com") {
@media screen and (min-width:1400px) {
#container { width: 1200px; }
div.l-header { width: 1100px!important; }
div.l-header #tabs_outter_container {
  position: static !important;
  width: 100% !important;
}
.pinned_sidebar_footer,
#toast_float {
  margin-left: -600px !important;
  width: 1200px !important;
}
#left_column {width: 925px !important; }
.post_full,
.notification { width: 840px !important; }
#post_buttons {
  width: 840px;
}
#post_buttons a{
  padding-left: 25px;
  padding-right: 25px;
}
#post_buttons a:first-child{
  padding-left: 0px;
}
#post_buttons a:last-child{
  padding-right: 0px;
}
.new_post_label .new_post_label_icon {
  left: 25px;
}
.new_post_label:first-child .new_post_label_icon {
  left: 0;
}
.notification { margin-bottom: 20px !important; }
.post .post_content .post_media img:not(.album_art) {
  width: 100% !important;
  height: auto !important;
}
.flipcard,
.flipcard_front,
.flipcard_back { width: 100% !important; }
.post_full.is_photoset .photoset .photoset_row {
  height: auto !important;
  width: 810px !important;
  margin-left: -10px !important;
}
.photoset_row a {
  margin: 0px !important;
  padding-left: 10px !important;
}
.photoset_row_1 a{ width: 100% !important; }
.photoset_row_2 a{ width: 50% !important; }
.photoset_row_3 a{ width: 33.33333333333333% !important; }
.photoset_row_4 a{ width: 25% !important; }
.photoset_row_5 a{ width: 20% !important; }
.post_full.is_photoset .photoset .photoset_row_1 {
  width: 800px !important;
  margin-left: 0px !important;
}
.photoset_row_1 a {
  padding-left: 0px !important;
}
.note_item {
  width: 710px !important;
}
.avatar a img {
  width: 64px;
  height: 64px;
}
.retro_video_preview{
  width: 800px !important;
  height: 450px !important;
}
.video .video_embed iframe{
  width: 800px !important;
  min-height: 450px !important;
}
.item{
  width: 800px !important;
}
.post .tags {
  width: 800px !important;
}
}
}
