css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("nnm.ru"){
    body{
        margin:0px !important;
    }
    #body{
        margin-top:-20px !important;
        margin-bottom:30px !important;
        width:100% !important;
        float:none !important;
    }
    #page {
        position:absolute !important;
        width:auto !important;
        left:165px !important;
        right:20px !important;
    }
    #topmenu, #topmenu ul{
        width:100% !important;
    }
    .docinfo{
        width:100% !important;
    }
    #topmenu li{
        background:#333 !important;
        width:16.59% !important;
    }
    .toolbar{
        width:100% !important;
    }
    #content{
        width:100% !important;
    }
    .content{
        width:100% !important;
    }
    #new{
        width:100% !important;
    }
    .new{
        width:100% !important;
    }
    .zakladki ul, .zakladki{
        width:100% !important;
    }
    .new-text{
        width:95% !important;
    }
    #MarketGid2471{
        display:none !important;
        height:0px !important;
    }
    .new-image-center{
        display:inline !important;
        max-width:60% !important;
        margin-left:20% !important;
        margin-right:20% !important;
    }
    .tags{
        display:block !important;
        clear:both !important;
    }
    #komments{
        display:block !important;
        clear:both !important;
    }
    .listing{
        margin-right:20% !important;
        margin-left:20% !important;
        width:60% !important;
    }
    .vchera, .str, .zavtra{
        float:left!important;
    }
    .zakladki li, .zakladki ul {
        float:right !important;
        margin-right:5px !important;
    }
    .zakladki li.zag {
        display:none;
    }
    .tagdoc{
        width:60% !important;
        margin-left:20% !important;
        margin-right:20% !important;
    }
    .new-addkomm, .tags, .new-menu, #new-reyt, #komments{
        width:60% !important;
        margin-left:20% !important;
        margin-right:20% !important;
    }

    .reclama,
    .left-banner,
    .right-banner,
    .center-banner,
    .left-up-banner,
    #top-banner,
    #footer,
    #bottom-banner {
        display: none !important;
    }
    #golosovalka
    {
        width:100% !important;
    }
    #golosovalka div
    {
        width:60% !important;
        margin-left:20% !important;
        margin-right:20% !important;        
    }
}
