@-moz-document url-prefix(https://twitter.) {

  /* hide trends module, who to follow module */
  .Trends,
  .wtf-module .flex-module,
  .WhoToFollow.is-visible {
    
    display: none;
  }
  
  /* hide promoted tweets and promoted accounts */
  .tweet.promoted-tweet,
  .promoted-account {
    
    display: none;
  }
  
  /* hide "view summary" links and the "..." button until tweet is hovered over. */
  .tweet a.details,
  .tweet .ProfileTweet-action--more {
   
    visibility: hidden;
  }
  
  .tweet:hover a.details,
  .tweet:hover .ProfileTweet-action--more {
    
    visibility: visible;
  } 
}
