css:
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix('https://www.tumblr.com')   {

/*
Victorian and pink
Pink colored version of victorian night
thirteentales
3
*/

body {
	background: grey;
background-image: url(http://24.media.tumblr.com/2ed95efa3dd51d6567fe3603ba6fa984/tumblr_mw0l7vzjI51r8s24ho2_400.png);
background-position: bottom left ;
background-attachment: fixed;
background-repeat: repeat;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
	background: #f0b2ad;
}

.post {
	background: white;
	border-radius: 20px;
}

.post_content{
	background: transparent !important;
}

#right_column:after {
	background: none !important;
}

.controls_section li {
	background: #e0a5a2;
        border-radius: 7px;
}


}
