@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("de.xfire.com"), domain("xfire.com"){

body{
background:url(http://www.abload.de/img/diabloiiiv3mljt5.png)center fixed!important;
color: #A9A9A9 !important;
}

.content_background {
background:url(http://www.abload.de/img/diabloiiiv3mljt5.png)center fixed!important; }

body.siteskin .siteskin_container {display:none!important; }


.widget h1, .main_column h1, .media-list-vertical h1 {
background: linear-gradient(to bottom, rgb(68, 68, 68) 0%, rgb(40, 40, 40) 49%, rgb(26, 26, 26) 50%, rgb(17, 17, 17) 100%) repeat scroll 0% 0% padding-box transparent;
background-image: linear-gradient(to bottom, rgb(68, 68, 68) 0%, rgb(40, 40, 40) 49%, rgb(26, 26, 26) 50%, rgb(17, 17, 17) 100%)!important;
}

.media-list-tiled a.media-item-more, .media-list-vertical a.media-item-more {
background:-moz-linear-gradient(center bottom , rgb(68, 68, 68) 0%, rgb(40, 40, 40) 49%, rgb(26, 26, 26) 50%, rgb(17, 17, 17) 100%) repeat scroll 0% 0% transparent;
background-image: -moz-linear-gradient(center bottom , rgb(68, 68, 68) 0%, rgb(40, 40, 40) 49%, rgb(26, 26, 26) 50%, rgb(17, 17, 17) 100%)!important;
}

.player_card .profile,.player_card > .currently_playing div.game-info  {
background-color: rgb(0,0,0)!important;
}

.main_column .list .item a, .widget.list .item a, .widget.list .item p.community_name, .widget.list .item a.community_name {
color: rgb(255, 97, 0)!important;
}

.main_column .list, .main_column .list .item:hover, .main_column .list .item.hover {
background: none repeat scroll 0% 0% rgb(0, 0, 0)!important;

}
.main_column .list .item {
background-color: rgb(30, 30, 30)!important;
}

.footer .content .right a.old {
font-size: 15px!important;
font-family: "Coda Caption",sans-serif !important;
}
.footer {
box-shadow:none!important;
}

.footer { 
background:none!important; }

.footer .content .right .download_btn a:hover {display:none!important; }

.footer .content .right .download_btn a { display:none!important; }

.widget.list .item p.currently_playing a {
color: rgb(255,255,255)!important;
}

a:link {color: rgb(255,255,255)  !important;}
a:hover{color: rgb(255, 97, 0)  !important;}
a {color: rgb(255, 97, 0)  !important;}
}
