css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://git."), 
url-prefix("https://git.") 
{
body {
background-color: rgb(25, 25, 25) !important;
}

.event_filter .filter_icon a { 
background: none repeat scroll 0% 0% rgb(49, 49, 49) !important;
}

.light-well, .description-block { 
background: none repeat scroll 0% 0% rgb(49, 49, 49) !important;
}

.project_clone_panel { 
background: none !important;
border: 0 !important;
}

.ui-box {
background: none repeat scroll 0% 0% rgb(35, 35, 35) !important;
}

.event-item .event_commits li.commit, .event-item .event-title, .commits-stat span{
color: rgb(182, 182, 182) !important;
}

.wiki p {
color: rgb(182, 182, 182) !important;
}

a, #new_issue_link, .btn.btn-new, .btn .btn-new{
    color: rgb(255, 255, 255) !important;
}

.main-nav {
    background: rgb(25, 25, 25) !important;
    text-shadow: none !important;
    }
    
    .ui-box .title {
    background: none! important;
    }
    
    .nav.nav-tabs li.active > a, ul.nav.nav-projects-tabs li.active > a, #sidebar-groups-tab:hover, .nav-pills.nav-stacked > .active > a {
    color: black !important;
    }
.light, .description-block {
color: rgb(182, 182, 182) !important;
}

h3.page-title, .save-project-loader h3 {
color: rgb(255, 255, 255) !important;
}

.event-item .event-body .event-note {
color: rgb(102, 102, 102) !important;
}

.well-list li:hover {

    background: rgb(50, 50, 50) !important;
    border-bottom: 1px solid rgb(250, 250, 250) !important;

}
.btn {
color: black !important;
 }
.btn.close_issue {

    color: #B94A48 !important;
    box-shadow: none !important;

}
.ui-box .title > a, .ui-box .title {
    text-shadow: 0px 1px 1px rgb(0, 0, 0) !important;
}

.merge-request.today, .issue.today {
    background: none repeat scroll 0% 0% rgb(60, 60, 60) !important;
    border-color: rgb(204, 238, 204) !important;
}

.ui-box .title { color: rgb(182, 182, 182) !important; }

.dropdown-menu>li>a, .ui-box .title .btn, .ui-box .title .btn>span{
color: black !important;
text-shadow: none !important;
box-shadow: none !important; } 

.nav>li>a:hover, .nav>li>a:focus, #new_issue_link {

 color: black !important;

}

.merge-request-title span {
color: rgb(255, 255, 255) !important; 
}

.common-note-form, .note-form-actions { 
background: none !important; 
border: none !important; 
}

.git-clone-holder {
    background: rgb(205, 205, 205);
}
.prepend-top-10 p {
color: rgb(255, 255, 255);
}
.chosen-single span {
color: black;
}
.event-item .event-body .event-note, .commit-title, .commit-description, .control-label {
    color: rgb(182, 182, 182) !important;
}
    .select2-chosen {
        color: rgb(0, 0, 0) !important;
    }
    .form-actions {
        background-color: transparent !important;
    }
    .merge-request.merged, .issue.merged {
        background: transparent !important;
    }
    .pull-right, .project-home-panel .project-home-title, .wiki  {
        color: white !important;
    }
    .merge-request.closed, .issue.closed {
        background: none repeat scroll 0% 0% rgba(59, 59, 59, 1) !important;
    }
    .pagination li a {
        color: rgb(0, 0, 0) !important;
    }
}
