css:
@-moz-document url-prefix("http://www.istockphoto.com/") {
#ZoomImageDiv{
  margin-bottom:20px;
}

#preview-section,#ZoomDroppableDiv,#ZoomDraggableDiv,#ZoomImageDiv{
 width:1235px!important; 
  height:auto!important;
  left:0!important;;
  top:0!important;
  display:block!important;
}

#s1,#s2,#s3{
  position:static!important;
}

#h1,#h2{
  display:none!Important;
}

#ActualImageDiv{
	text-align:left;
}

.search-item.small {
  width:auto!important;
}

.search-item.small .item-default{
  height:auto!important;
}
    
.search-item.small .item-default img{
	min-height:200px!important;
    max-height:300px!important;
  	min-width:200px!important;
    max-width:300px !important;
    width:auto!important;
}
.search-item.small .search-item-clickable-area {
    width:280px;
    height:auto;
}
}
