div#content div#menu div#menu_r {background-image:none !important;}
div#content div#content_inner {margin:2% !important; padding:2% !important;}
div#content_text div.forum_main table.forumline tbody tr th.thTop {display:none;}
div#content_text div.forum_main table.forumline tbody tr th.thCornerR {display:none;}
