css:
@-moz-document url-prefix('http://digg.com/reader'), url-prefix('https://digg.com/reader') {

  body {
    font-family: "Helvetica Neue", Helvetica, sans-serif;
  }

  .story-detail-view-container {
    font-family: 'Avenir Next', Georgia, "Times New Roman", Times, serif;
  }

  .story-detail-content {
    width: 760px;
  }

  .detail-body {
    font-family: inherit;
    font-size: 20px;
    line-height: 1.6em;
  }

  .story-title {
    font-size: 42px;
  }
  
}
