css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("xkcd.com") {
    body{
        background-color:#fff !important;       
}
    #topContainer,
    #bottom{
        display:none;
}
    #middleContainer > :nth-child(2),
    #middleContainer > :nth-child(5),
    #middleContainer > :nth-child(6){
        display:none;
}
    #middleContainer{
        margin-top:-11px !important;
        padding:0px 0px !important;
        color:#fff;
        width: 100% !important;
        left:0% !important;
        margin-left:0 !important;
        position: absolute !important;
}
    #ctitle{
        color:#000;
        margin:0 !important;
}
    .box{
        border-width:0px !important;
        border-color: #fff;
}
    .comicNav > :nth-child(1),
    .comicNav > :nth-child(3),
    .comicNav > :nth-child(5){
        display:none !important;
}
    #transcript {
        display: block !important;
        border: 1px solid #999;
        margin: 1em 60px;
        white-space: pre-wrap;
        font-variant: normal;
        padding: 1em;
        position: relative;
        border-radius: .5em;
        color:#000;
        margin-top:-40px;
}
    #transcript:before {
        content: 'Transcript:';
        position: absolute;
        top: -1px;
        left: -1px;
        background-color: #999;
        color: #fff;
        padding: 0 .5em;
        border-radius: .5em 0 .5em 0;
}
    #transcript:empty {
        border: none;
}

    #transcript:empty:before {
        content: 'Transcript is empty :(';
        position: static;
}

}
