css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

tabmodalprompt {
    background: rgba(190, 190, 190, .7) !important;
}

tabmodalprompt .mainContainer {
    background: transparent !important;
    box-shadow: 1px 1px 5px rgba(0, 0, 0, .75), 1px 1px 25px rgba(0, 0, 0, .5);
    border: 0.5px solid rgba(100, 100, 100, .9) !important;
    max-height: 500px !important;
    width: auto !important;
    border-radius: 10px !important;
}

tabmodalprompt .topContainer,tabmodalprompt .buttonContainer {
    background: rgba(0, 0, 0, .8) !important;
    color: white !important;
}

tabmodalprompt .topContainer {
    border-radius: 10px 10px 0 0 !important;
}

tabmodalprompt .buttonContainer {
    border-radius: 0 0 10px 10px !important;
}
